    /**
     * Retorna o viewBox correto para cada módulo baseado no ícone
     */
    public static function getViewBox(string $moduleName): string
    {
        $viewBoxes = [
            'Agua' => '0 0 1024 1024',
            'Iluminacao' => '0 0 512 512',
            'Estradas' => '0 0 512 512',
            'Pocos' => '0 0 410.762 410.762',
            'Pessoas' => '0 0 24 24',
            'Demandas' => '0 0 961.396 961.396',
            'Equipes' => '0 0 512 512',
            'Funcionarios' => '0 0 480.16 480.16',
            'Materiais' => '0 0 24 24',
            'Ordens' => '0 0 24 24',
            'Localidades' => '0 0 502 502',
            'Notificacoes' => '0 0 24 24',
            'Relatorios' => '0 0 24 24',
            'Homepage' => '0 0 24 24',
            'Chat' => '0 0 24 24',
            'CAF' => '0 0 24 24',
            'ProgramasAgricultura' => '0 0 24 24',
            'Avisos' => '0 0 24 24',
            'Blog' => '0 0 24 24',
        ];

        return $viewBoxes[$moduleName] ?? '0 0 24 24';
    }

    /**
     * Retorna se o ícone usa fill ou stroke
     */
    public static function getIconType(string $moduleName): string
    {
        $fillIcons = ['Agua', 'Iluminacao', 'Estradas', 'Pocos', 'Pessoas', 'Demandas', 'Equipes', 'Funcionarios',
            'Localidades', 'Avisos'];

        return in_array($moduleName, $fillIcons) ? 'fill' : 'stroke';
    }
